app.controller('modalCtrl', function ($scope, $uibModal, $log) {
  $scope.onSuccess = function (e) {
    e.clearSelection();
  };

  $scope.open = function (size) {

    var modalInstance = $uibModal.open({
      templateUrl: 'modalEventEdit.html',
      size: size,
      backdrop: true,
      backdropClass: 'modal__overlay'
    });
  };

});

